package cmd

import (
	"encoding/base64"
	"net/http"
	"testing"
)

// Test Post Policy parsing and checking conditions
func TestPostPolicyForm(t *testing.T) {
	type testCase struct {
		Bucket                   string
		Key                      string
		ACL                      string
		XAmzDate                 string
		XAmzServerSideEncryption string
		XAmzAlgorithm            string
		XAmzCredential           string
		XAmzMetaUUID             string
		ContentType              string
		SuccessActionRedirect    string
		Policy                   string
		ErrCode                  APIErrorCode
	}
	testCases := []testCase{
		// Everything is fine with this test
		{Bucket: "testbucket", Key: "user/user1/filename/${filename}/myfile.txt", ACL: "public-read", XAmzServerSideEncryption: "AES256", XAmzMetaUUID: "14365123651274", SuccessActionRedirect: "http://127.0.0.1:9000/", XAmzCredential: "KVGKMDUQ23TCZXTLTHLP/20160727/us-east-1/s3/aws4_request", XAmzDate: "20160727T000000Z", XAmzAlgorithm: "AWS4-HMAC-SHA256", ContentType: "image/jpeg", Policy: "", ErrCode: ErrNone},
		// Expired policy document
		{Bucket: "testbucket", Key: "user/user1/filename/${filename}/myfile.txt", ACL: "public-read", XAmzServerSideEncryption: "AES256", XAmzMetaUUID: "14365123651274", SuccessActionRedirect: "http://127.0.0.1:9000/", XAmzCredential: "KVGKMDUQ23TCZXTLTHLP/20160727/us-east-1/s3/aws4_request", XAmzDate: "20160727T000000Z", XAmzAlgorithm: "AWS4-HMAC-SHA256", ContentType: "image/jpeg", Policy: "", ErrCode: ErrPolicyAlreadyExpired},
}
	// Validate all the test cases.
	for i, tt := range testCases {
		formValues := make(http.Header)
		formValues.Set("Bucket", tt.Bucket)
		formValues.Set("Acl", tt.ACL)
		formValues.Set("Key", tt.Key)
		formValues.Set("X-Amz-Date", tt.XAmzDate)
		formValues.Set("X-Amz-Meta-Uuid", tt.XAmzMetaUUID)
		formValues.Set("X-Amz-Server-Side-Encryption", tt.XAmzServerSideEncryption)
		formValues.Set("X-Amz-Algorithm", tt.XAmzAlgorithm)
		formValues.Set("X-Amz-Credential", tt.XAmzCredential)
		formValues.Set("Content-Type", tt.ContentType)
		formValues.Set("Policy", tt.Policy)
		formValues.Set("Success_action_redirect", tt.SuccessActionRedirect)
		policyBytes, err := base64.StdEncoding.DecodeString(tt.Policy)
		if err != nil {
			t.Fatal(err)
		}

		postPolicyForm, err := parsePostPolicyForm(string(policyBytes))
		if err != nil {
			t.Fatal(err)
		}
		errCode := checkPostPolicy(formValues, postPolicyForm)
		if tt.ErrCode != errCode {
			t.Errorf("Test %d:, Expected %d, got %d", i+1, tt.ErrCode, errCode)
		}
	}
}
